import React, { useEffect, useState } from 'react';
import Web3Modal from "web3modal";
import { useSelector, useDispatch } from 'react-redux'
import { FaWindowClose } from 'react-icons/fa';
import { AiOutlineMenu } from 'react-icons/ai';
import { Link } from "react-router-dom";
import { ethers } from "ethers";
import { Container, Row, Col, Button, Modal } from 'react-bootstrap';
// import 'bootstrap/dist/css/bootstrap.min.css'
import { FaClone, FaWallet } from 'react-icons/fa';
import WalletConnectProvider from '@walletconnect/web3-provider/dist/umd/index.min.js';
// import CoinbaseWalletSDK from "@coinbase/wallet-sdk";
import { Networks } from '../networks/Networks';
import { TEAM } from '../contracts/';
import { toHex, truncateAddress } from '../utils/functions.js';
import { ConnectContract, ToEthers, FromEthers, CurrencyFormat, loading } from '../lib/';
import { images } from '../constants/';
// import { Menu } from '../components/';

import { GET_STATE, setAddress, setChainId, setIsTeam, setModalWallet } from '../redux/user'



const providerOptions = {
  injected: {
    display: {
      logo: 'data:image/png;base64,',
      name: 'Metamask',
      description: 'Connect with the provider in your Browser',
    },
    package: null,
  },
  walletconnect: {
    package: WalletConnectProvider,
    options: {
      rpc: {
          97: Networks["97"].rpcUrls
      },
      chainId: 97
  }
  }
};

export const web3Modal = new Web3Modal({
  cacheProvider: true,
  // disableInjectedProvider: true,
  providerOptions
});

const connectProxyNetwork = (_url) => {
  const proxyNetwork = _url;
  const proxyProvider = new ethers.providers.JsonRpcProvider(proxyNetwork);
  const Provider = {
      provider: proxyProvider,
      signer: proxyProvider,
      address: "0x0"
  }
  return Provider;
}

export const getProvider = async () => {
  // console.log(web3Modal)
  if (!web3Modal.cachedProvider) {
    return
  }
    const conn = await web3Modal.connect();
    const provider = new ethers.providers.Web3Provider(conn);
    const signer = provider.getSigner();
    const accounts = await provider.listAccounts();
    const network = await provider.getNetwork();
    return {signer: signer, provider: provider, accounts: accounts[0], network: network};  

}



export const  Providers = () => {

  const dispatch = useDispatch();

  
  const isTeam =  GET_STATE('user', 'isTeam');
  const IsOpen =  GET_STATE('user', 'modalWallet');

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  

  const [provider, setProvider] = useState();
  const [library, setLibrary] = useState();
  const [account, setAccount] = useState();
  const [signature, setSignature] = useState("");
  const [error, setError] = useState("");
  const [chainId, setChain] = useState();
  const [network, setNetwork] = useState();
  const [message, setMessage] = useState("");
  const [signedMessage, setSignedMessage] = useState("");
  const [verified, setVerified] = useState();
  const [balance, setBalance] = useState();
  const [nativeToken, setNativeToken] = useState();
  const [explorer, setExplorer] = useState();


  const connectWallet = async () => {
    try {
      const provider = await web3Modal.connect();
      const library = new ethers.providers.Web3Provider(provider);
      const accounts = await library.listAccounts();
      const network = await library.getNetwork();
      const signer = library.getSigner();
      const balanceOf = CurrencyFormat(FromEthers(await signer.getBalance()));
      if(Networks[network["chainId"]]){
        setNetwork(Networks[network["chainId"]].chainName);
        setNativeToken(Networks[network["chainId"]].nativeCurrency.symbol);
        setExplorer(Networks[network["chainId"]].blockExplorerUrls);
      }else{
        setNetwork("Wrong network")
      }
      setBalance(balanceOf);
      setProvider(provider);
      setLibrary(library);
      // setUserAddress(accounts[0]);
      dispatch(setAddress(accounts[0]));
      dispatch(setChainId(network.chainId));
      dispatch(setIsTeam(TEAM.includes(accounts[0])));
      if (accounts) setAccount(accounts[0]);
      setChain(network.chainId);

      
    } catch (error) {
      setError(error);
    }
  };

  const handleNetwork = (e) => {
    const id = e.target.value;
    setNetwork(Number(id));
  };

  const handleInput = (e) => {
    const msg = e.target.value;
    setMessage(msg);
  };

  const switchNetwork = async () => {
    try {
      await library.provider.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: toHex("97") }]
      });
    } catch (switchError) {
      if (switchError.code === 4902) {
        try {
          await library.provider.request({
            method: "wallet_addEthereumChain",
            params: [Networks[toHex(chainId)]]
          });
        } catch (error) {
          setError(error);
        }
      }
    }
  };

  const signMessage = async () => {
    if (!library) return;
    try {
      const signature = await library.provider.request({
        method: "personal_sign",
        params: [message, account]
      });
      setSignedMessage(message);
      setSignature(signature);
    } catch (error) {
      setError(error);
    }
  };

  const verifyMessage = async () => {
    if (!library) return;
    try {
      const verify = await library.provider.request({
        method: "personal_ecRecover",
        params: [signedMessage, signature]
      });
      setVerified(verify === account.toLowerCase());
    } catch (error) {
      setError(error);
    }
  };

  const refreshState = () => {
    setAccount();
    setSignature("");
    setVerified(undefined);
  };

  const disconnect = async () => {
    web3Modal.clearCachedProvider();
    refreshState();
    handleClose();
  };


  useEffect(() => {
    if (web3Modal.cachedProvider) {
      connectWallet();
    }
  }, []);

  useEffect(() => {
    if (provider?.on) {
      const handleAccountsChanged = (accounts) => {
        console.log("accountsChanged", accounts);
        if (accounts) setAccount(accounts[0]);
      };

      const handleChainChanged = (_hexChainId) => {
        setChainId(_hexChainId);
      };

      const handleDisconnect = () => {
        console.log("disconnect", error);
        disconnect();
        
      };

      provider.on("accountsChanged", handleAccountsChanged);
      provider.on("chainChanged", handleChainChanged);
      provider.on("disconnect", handleDisconnect);

      return () => {
        if (provider.removeListener) {
          provider.removeListener("accountsChanged", handleAccountsChanged);
          provider.removeListener("chainChanged", handleChainChanged);
          provider.removeListener("disconnect", handleDisconnect);
        }
      };
    }
  }, [provider]);

  useEffect(() => {

    
  },[])

  const changeNetwork = () => {
    switchNetwork().then(() => {
      window.location.reload();
    })
  }
  // const [IsOpen, setIsOpen] = useState(false);
  const OpenModal = () => {
    dispatch(setModalWallet(true));
      
  }
  const CloseModal = () => {
    dispatch(setModalWallet(false));
      
  }

  const WalletConnected = () => {
    return(
      <div className="flex flex-col md:flex-row items-center w-full justify-center gap-5">

              {network && (
              <>
              {(chainId == 97) ?
              <div className="w-full py-4 px-3 bg-gradient-1 mt-5 text-lg flex items-center justify-center flex-nowrap"><img src={images.BNB} className="h-5 w-5 mr-2" /> {network}</div>
              :
              <div className="w-full  py-4 px-3 bg-red-500 mt-5 cursor-pointer text-lg flex items-center justify-center flex-nowrap" onClick={changeNetwork}>Erro na rede</div>
            }
              </>
              )}
              {/* {balance && (
              <>
              <div className="p-3 bg-gradient-1 text-lg flex items-center justify-center flex-nowrap">{balance} {nativeToken}</div>
              </>
              )} */}
              <div className="w-full  py-4 px-3 bg-gradient-1 mt-5 text-lg flex items-center justify-center flex-nowrap hover:bg-gradient-2 ">
                <a href={`${Networks[chainId].blockExplorerUrls}/address/${account}`} target="_blank" className="text-lg">{truncateAddress(account)}</a>
                
              </div>
              <div onClick={disconnect} className="w-full mt-5  flex py-5 cursor-pointer px-3 bg-gradient-1 text-lg items-center justify-center hover:bg-gradient-2 "><FaWindowClose className="mr-3" /> Disconnect</div>
            </div>
    )
  }

  const ModalMobile = () => {
    return(
      <>
      <div className={`w-screen  h-modal inset-0 m-auto fixed flex z-[5000] justify-center items-center overflow-x-hidden overflow-y-auto ${!IsOpen && 'hidden'}`}>
        <div className=" bg-hero opacity-75 w-screen h-modal inset-0 m-auto fixed flex z-[10] justify-center items-center overflow-x-hidden overflow-y-auto"></div>
        <div className="bg-color-3 border-[1px] border-color-4 text-pallete-9 inset-0 rounded-lg z-[100] shadow-lg p-8  animate-slide-up m-auto w-full md:w-4/5 ">
            <div className="relative w-full">
            <div className="flex items-start justify-between p-4 rounded-t dark:border-gray-600 ">
                      <h3 className="text-xl font-semibold text-white">
                      <img src={images.logo} className="w-[250px] lg:w-[200px]" />
                      </h3>
                      <button onClick={CloseModal} type="button" className="text-color-4 bg-transparent hover:bg-gray-200 hover:text-gray-900 rounded-lg text-sm p-1.5 ml-auto inline-flex items-center dark:hover:bg-gray-600 dark:hover:text-white" data-modal-toggle="defaultModal">
                          <svg aria-hidden="true" className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd"></path></svg>
                          <span className="sr-only">Close modal</span>
                      </button>
                  </div>
                  <hr className="sc-bqyKOL gSuxaX"></hr>
                  {/* {(isTeam) &&
                  <Menu />
                  } */}
                  <WalletConnected />
            </div>
        </div>
        </div>
      </>
    )
  }


    return (
      <>
      <div className="flex flex-col items-center flex-nowrap justify-end w-full md:flex-row m-2 ">
      {!account ? (
        <div className="flex items-center justify-center bg-gradient-1 hover:bg-gradient-2 w-full">
            <button className="p-3 mx-3 text-lg" onClick={connectWallet}>Connect Wallet</button>
            </div>
          ) : (
            <>
            {/* <div className="hidden md:flex" ><WalletConnected /></div> */}
            <AiOutlineMenu onClick={OpenModal} className="cursor-pointer hover:text-hover text-4xl" />
            <ModalMobile />
            </>
          )}
</div>  
      </>
      )

}